mapper.update:
update address
set name = #{name,jdbcType=VARCHAR},
  detail = #{detail,jdbcType=VARCHAR},
  phone = #{phone,jdbcType=VARCHAR},
  user_id = #{userId,jdbcType=VARCHAR}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select id, name, detail, phone, user_id
from address
where id = #{id,jdbcType=VARCHAR}
