mapper.insert:
insert into admin (id, usernane, password
  )
values (#{id,jdbcType=VARCHAR}, #{usernane,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR}
  )

mapper.update:
update admin
set usernane = #{usernane,jdbcType=VARCHAR},
  password = #{password,jdbcType=VARCHAR}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select id, usernane, password
from admin
where id = #{id,jdbcType=VARCHAR}
