mapper.insert:
insert into business (id, username, password, 
  name, address, phone, 
  registertime, start_price, distribution_fee
  )
values (#{id,jdbcType=VARCHAR}, #{username,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR}, 
  #{name,jdbcType=VARCHAR}, #{address,jdbcType=VARCHAR}, #{phone,jdbcType=VARCHAR}, 
  #{registertime,jdbcType=DATE}, #{startPrice,jdbcType=DECIMAL}, #{distributionFee,jdbcType=DECIMAL}
  )

mapper.select:
select id, username, password, name, address, phone, registertime, start_price, distribution_fee
from business
where id = #{id,jdbcType=VARCHAR}
