mapper.update:
update order_detail
set order_id = #{orderId,jdbcType=VARCHAR},
  goods_id = #{goodsId,jdbcType=VARCHAR},
  goods_count = #{goodsCount,jdbcType=INTEGER}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select id, order_id, goods_id, goods_count
from order_detail
where id = #{id,jdbcType=VARCHAR}
