mapper.update:
update reduce
set business_id = #{businessId,jdbcType=VARCHAR},
  achieve_money = #{achieveMoney,jdbcType=DECIMAL},
  reduce_money = #{reduceMoney,jdbcType=DECIMAL}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select id, business_id, achieve_money, reduce_money
from reduce
where id = #{id,jdbcType=VARCHAR}
