mapper.update:
update shop_cart
set goods_id = #{goodsId,jdbcType=VARCHAR},
  user_id = #{userId,jdbcType=VARCHAR},
  goods_count = #{goodsCount,jdbcType=INTEGER}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select id, goods_id, user_id, goods_count
from shop_cart
where id = #{id,jdbcType=VARCHAR}
